insert into app_pages(menu_id,menu_code,sl_page_id,title,image,content,post_date,post_time,date) values (2,'001001',13,'','','<p style="line-height:1.5em;text-align:justify;margin-bottom:10px;"><span style="font-size:13px;color:#383838;font-family:宋体">&nbsp;&nbsp;&nbsp;&nbsp;<span style="font-size:13px;color:#383838;font-family:宋体,simsun">盛隆电气集团成立于1979年，是中国领先的提供输配电设备、系统及解决方案的厂商。盛隆电气集团业务遍布中国所有省市及海外20多个国家，拥有4000多名员工，是北京、华中、华北市场成套输配电设备领域的引领厂商。</span></span></p><p style="line-height:1.5em;text-align:left;margin-top:auto;margin-bottom:10px;"><span style="font-size:13px;color:#383838;font-family:宋体,simsun">&nbsp;&nbsp;&nbsp;&nbsp;盛隆电气集团下属12个子公司，5个工厂，1个研究所，20多个分公司或办事处，其中北京公司和武汉公司分别在华北和华中市场位居引领地位。武汉公司当选为2012年武汉百强企业。集团在北京、武汉、上海、东莞和重庆建有大型生产工厂。</span></p><p style="line-height:1.5em;text-align:left;margin-top:auto;margin-bottom:10px;"><span style="font-size:13px;color:#383838;font-family:宋体,simsun">&nbsp;&nbsp;&nbsp;&nbsp;盛隆电气集团在成套输配电设备、电力监控、电力智能、电力节能四大领域有广泛的产品线和解决方案，这些产品和解决方案致力于使客户发电和使用电力更加安全、可靠、节能和环保。面对日益增长的能源需求和节能减排的严峻挑战，盛隆电气集团通过先进的产品和解决方案，帮助客户提高电力使用效率，降低电力能耗，减少碳排放量，创造良好的社会、经济和环境效益。盛隆电气集团的产品已广泛应用于基础设施、能源、工业、商业、楼宇、民用住宅等市场，如国家电厂、铁路、高速公路、地铁、机场、奥运场馆等国家重点工程。</span></p><p style="line-height:1.5em;text-align:left;margin-top:auto;margin-bottom:10px;"><span style="font-size:13px;color:#383838;font-family:宋体,simsun">&nbsp;&nbsp;&nbsp;&nbsp;盛隆电气集团一贯重视科技研发以及自主创新，在电力智能及电力节能领域获得了多项国家专利和软件著作权。集团长期坚持走产学研一体化道路，与清华大学、北京大学、中科院电工研究所等单位有20多年的良好合作关系。1999年集团公司在北京人民大会堂成立盛隆电气北京研究所，中国工程院院士、国家著名电力专家顾国彪院士担任研究所所长。集团公司在立足于自主创新的基础上，在技术领域与知名跨国公司如ABB、西门子、施耐德等也有着良好的合作关系。&nbsp;</span></p><p style="line-height:1.5em;text-align:left"><span style="font-size:13px;color:#383838;font-family:宋体,simsun">&nbsp;&nbsp;&nbsp;&nbsp;盛隆电气集团坚持以人为本，高度重视引进人才、培养人才、留住人才，于2010年建立了盛隆大学北京大型培训基地。盛隆电气集团用先进的技术为客户提供产品的同时，也积极承担社会责任，得到了社会的广泛认可。</span></p><p style="line-height:1.5em;text-align:left"><br /></p><p style="line-height:22px;text-align:left"><span style="color:#383838;font-family:"></span></p><p style="line-height:1.5em;text-align:justify"><span style="font-size:13px;font-family:宋体,simsun"></span></p><p style="line-height:1.5em;text-align:justify"><span style="font-size:13px;font-family:宋体,simsun"></span></p>','2014-01-12 15:08:47',1389510526,1389510526);
insert into app_pages(menu_id,menu_code,sl_page_id,title,image,content,post_date,post_time,date) values (3,'001002',14,'','','<p style="line-height:1.5em;"><span style="font-family:宋体;font-size:13px;">&nbsp;&nbsp;&nbsp;&nbsp;盛隆电气集团于1985年成立党支部，2008年建立党委，下设5个党支部。近年来，盛隆电气集团一直以学习型党支部建设为基础，确立党组织的政治核心地位，发挥党员在党组织建设和集团建设中的主体作用，积极探索党组织建设的新模式，新方法。盛隆秉承“为四化建设出力，为人民造福流汗”的宗旨，以党组织的凝聚力和号召力，激发党支部和党员参与企业决策以及经营管理的积极性和创造性。真正做到企业决策有党的声音，企业管理有党的身影。一直以来，集团党支部加强员工的思想政治工作，健全完善学习型党组织建设的制度体系，努力打造具有盛隆特色的党组织文化，倾力建设活力和谐的盛隆电气集团。</span></p><p>&nbsp;</p>','2014-01-12 15:08:48',1389510526,1389510526);
insert into app_pages(menu_id,menu_code,sl_page_id,title,image,content,post_date,post_time,date) values (5,'001003001',30,'','','<p><strong><span style="font-size:13px;font-family:宋体;color:red">一个目标：百年辉煌</span></strong></p><p>&nbsp;</p><p><span style="font-size:13px;font-family:宋体">2009</span><span style="font-size:13px;font-family:宋体">年5月2日，<span style="color:#383838">盛隆电气创建三十周年庆典大会在北京钓鱼台国宾馆隆重举行。会上，谢元德</span>董事长<span style="color:#383838">提出了“诚信攀越百年峰，百年辉煌我盛隆”的战略目标。</span></span></p><p>&nbsp;</p><p><span style="font-size:13px;font-family:宋体">2004</span><span style="font-size:13px;font-family:宋体">年11月13日，谢元德董事长在“盛隆电气百年不倒武汉定位研讨会”上提出了盛隆电气百年不倒的总目标。</span></p><p>&nbsp;</p><p style="text-align:left;line-height:normal"><span style="font-size:13px;font-family:宋体;color:#1f497d">“盛隆电气百年不倒是我最大的需要。</span></p><p style="text-align:left;line-height:normal"><span style="font-size:13px;font-family:宋体;color:#1f497d">有技术的传授技术，有经验的传授经验，这是我最大的希望。</span></p><p style="text-align:left;line-height:normal"><span style="font-size:13px;font-family:宋体;color:#1f497d">年轻的朋友们，快成长，快成长，快快成长，这是我长久的期盼。</span></p><p style="text-align:left;margin:0px;line-height:normal"><span style="font-size:13px;font-family:宋体;color:#1f497d">互敬互爱，相互关怀，大家开心，人人发财，看到你们一代一代，事业有成，夫妻恩爱，这是我最大的心愿！”</span></p><p style="text-align:right"><strong><span style="font-size:13px;font-family:宋体">摘自&nbsp;谢元德董事长2004年《说句心里话》</span></strong></p><p>&nbsp;</p><p>&nbsp;</p><p><strong><span style="font-size:13px;font-family:宋体;color:red">三个战略：“五营三角连环阵”市场战略、“十万大军”人才战略、文化制度战略</span></strong></p><p>&nbsp;</p><p><img title="map" style="height:350px;float:right;width:350px" border="0" hspace="0" src="http://m.shenglong-electric.com.cn/uploads/20130326/13642794016969.jpg" width="350" height="350" /></p><p><strong><span style="font-size:13px;font-family:宋体;color:red">“五营三角连环阵”市场战略</span></strong></p><p>&nbsp;</p><p><strong><span style="font-size:13px;font-family:宋体">第一战役（2008年）</span></strong></p><p><span style="font-size:13px;font-family:宋体">分兵七路扎据点，百员大将抢外滩</span></p><p><strong><span style="font-size:13px;font-family:宋体">第二战役（2009年）</span></strong></p><p><span style="font-size:13px;font-family:宋体">〇九三角大汇演，北东中部大营连</span></p><p><strong><span style="font-size:13px;font-family:宋体">第三战役（2010年）</span></strong></p><p><span style="font-size:13px;font-family:宋体">京汉破竹连珠响，扎营重庆战东莞</span></p><p><strong><span style="font-size:13px;font-family:宋体">第四战役（2011年）</span></strong></p><p><span style="font-size:13px;font-family:宋体">实现省会全覆盖，五营三角核心圈</span></p><p><strong><span style="font-size:13px;font-family:宋体">第五战役（2012年</span></strong><span style="font-size:13px;font-family:宋体">）</span></p><p><span style="font-size:13px;font-family:宋体">打牢国内百年基，迈出国门试深浅</span></p><p>&nbsp;</p><p><strong><span style="font-size:13px;font-family:宋体;color:red">“十万大军”人才战略</span></strong></p><p>&nbsp;</p><p><strong><span style="font-size:13px;font-family:宋体;letter-spacing:0px">盛隆电气集团组织、人事、管理制度（一号制度）</span></strong><strong><span style="font-size:13px;font-family:宋体">第二条&nbsp;：</span></strong><span style="font-size:13px;font-family:宋体"></span></p><p><span style="font-size:13px;font-family:宋体;letter-spacing:0px">关心、支持、帮助就业、创业是盛隆企业文化的核心。盛隆电气是一个创业平台，千方百计帮助每一名盛隆员工成才、创业、做大、做强。</span></p><p>&nbsp;</p><p><span style="font-size:13px;font-family:宋体;color:#1f497d">“</span><span style="font-size:13px;font-family:宋体;color:#1f497d">我了解现在的年轻人生活压力很大，我们公司年轻人多，在当下这个大环境，都很不容易。我们既然有这个企业，大家信任这个企业，进了这个企业，这个企业一定要负责任，对这些年轻人负责任。作为一个企业的负责人，特别需要重视年轻人的现实问题，根据大家的切身需要去思考。年轻人有需要、有干劲，有很大的积极性，那么怎么去引导大家合理的需要，怎么去激励大家的干劲，怎么找寻一些办法，一起去创造、去发展？在企业发展的每一阶段，必须明确、树立前进的方向，奋斗的目标。如何确定奋斗的方向和目标？一是要把个人的目标与企业的目标很好地统一起来。企业的目标是为满足员工的目标而设的。二是目标不能太虚，但是一定要积极。当然也要稳妥...”</span></p><p style="text-align:right"><strong><span style="font-size:13px;font-family:宋体">董事长2012年1月16日在武汉工厂战略总动员会议上的讲话（摘要）</span></strong></p><p style="text-align:right">&nbsp;</p><p><span style="font-size:13px;font-family:宋体;color:#1f497d">“我始终不同意当一辈子工人的观念，应当大大拓宽他们的职业发展道路。给他们更广阔的成长空间，更多的发展机遇与道路选择。使他们的个人前途更加宽广更有保障。这是最切实的帮助，最根本的关心，是真正的以人为本，对青年的成长和人生真正负责。当然，这也是为公司培养优秀人才，增加宝贵财富。事实证明，年青人经过一段时间生产车间艰苦劳动的锻炼磨砺，不仅学习了生产技术，掌握了一些专业技能，而且更是对自己思想、品性的涵养提高、多方面素质的培养修成，发挥了熔炉作用。对他们今后长期的人生发展十分有利，打下了一个很好的根基。公司内外，普遍称赞这些经过车间劳动锻炼，转行做营销的年青人，有梦想，敢追求，能吃苦，懂技术，重诚信。他们能走向成功，绝非偶然，应当感谢车间劳动给他们的锻炼和磨砺。</span></p><p><span style="color:#7f7f7f"></span></p><p><span style="font-size:13px;font-family:宋体;color:#1f497d">正是基于这些成功的经验，我们认为，一线的生产车间，不仅可以出电气产品，更能出各种人才。我们完全可以通过努力，把一线生产厂变成一所半工半读式的大学校。如果实施的好，将为盛隆年青人更好、更快地成长、成才、成功，打开方便之门，铺设一条通向光明未来的快车道。同时成批量、成规模地、源源不断地为集团输送优秀人才。这对公司的长远发展，是具有战略意义的。”</span></p><p style="text-align:right;line-height:150%"><strong><span style="font-size:13px;font-family:宋体;letter-spacing:0px;line-height:150%">董事长2010年北京庆功会讲话</span></strong><strong><span style="font-size:13px;font-family:宋体;line-height:150%">（摘要）</span></strong></p><p style="text-align:right">&nbsp;</p><p style="text-align:center"><img title="recai2" style="height:350px;width:350px" border="0" hspace="0" src="http://m.shenglong-electric.com.cn/uploads/20130326/13642710701022.jpg" width="350" height="350" /></p><p><strong><span style="font-size:13px;font-family:宋体;color:red">文化制度战略</span></strong></p><p>&nbsp;</p><p><span style="font-size:13px;font-family:宋体">盛隆文化是中国优秀民族文化的继承，发扬、具体化。我们重视从中国民族传统文化中汲取精华，建设盛隆文化。早期：为四化建设，为人民造福；九十年代：盛世同襄盛举，隆情共创隆图；盛隆企业文化规定的：表层文化：温暖和谐，昂扬向上；中层文化：科学合理，易知易行；深层文化：追求卓越，奉献社会；我们一直提倡贯彻为国为民，敢想敢干，以人为本，科学发展。这些都是盛隆文化的集中表现，体现了盛隆实际与中国优秀民族传统文化的结合。</span></p><p>&nbsp;</p><p><span style="font-size:13px;font-family:宋体">（请了解公司积淀30年&nbsp;十大代表性文化）</span></p><p>&nbsp;</p>','2014-01-12 15:08:48',1389510526,1389510526);
insert into app_pages(menu_id,menu_code,sl_page_id,title,image,content,post_date,post_time,date) values (6,'001003002',31,'','','<p style="line-height:19px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">中期目标：三大战略给力200亿</span></strong></p><p style="line-height:19px;">&nbsp;</p><p><strong><span style="color:black;font-family:宋体;font-size:13px;">武汉工厂战略的内容和意义</span></strong></p><p style="line-height:29px;"><span style="color:black;font-family:宋体;font-size:13px;">1.</span><span style="color:black;font-family:宋体;font-size:13px;">建设中国电气开关行业现代化的第一车间。</span></p><p><span style="font-family:宋体;font-size:13px;">2.</span><span style="font-family:宋体;font-size:13px;">打造盛隆大学培养现代企业人才第一课堂。</span></p><p style="line-height:29px;">&nbsp;</p><p style="line-height:29px;"><strong><span style="color:black;font-family:宋体;font-size:13px;">玉琼团队战略的意义、特点和价值</span></strong></p><p><span style="font-family:宋体;font-size:13px;">1.</span><span style="font-family:宋体;font-size:13px;">经营方式的转型升级：由资金投入式转为“能信营创式”</span></p><p><span style="font-family:宋体;font-size:13px;">2.</span><span style="font-family:宋体;font-size:13px;">团队的组织计划性、市场定位的准确性、营销模式与对象的战略性、队伍发展的可持续性。</span></p><p><span style="font-family:宋体;font-size:13px;">3.</span><span style="font-family:宋体;font-size:13px;">体现重庆会议团队建设目标。以盛隆文化为指导，建设“双精”领导集体，打造“共赢”营销团队，启动“百总”培养工程。打造永不满足现状、勇于学习、善于学习，能够不断充实、提高自己，不断向新的奋斗目标攀登进取的团队。</span></p><p><span style="font-family:宋体;font-size:13px;">4.</span><span style="font-family:宋体;font-size:13px;">体现出生产管理工作的价值，工厂培养人才，成就高级人才。</span></p><p><span style="font-family:宋体;font-size:13px;">5.</span><span style="font-family:宋体;font-size:13px;">体现公司文化：个人主义与集体主义的有机结合。</span></p><p><span style="font-family:宋体;font-size:13px;">6.</span><span style="font-family:宋体;font-size:13px;">营销引导生产，生产决定成败。<span style="color:black;">能信营创的团队，对自己的生产搭档，在如何创新、提高，适销对路，满足市场、顾客方面，有引导的作用，有引导的能力。由此体现到生产决定成败。没有生产做好产品，控制成本，提供好的服务，不可能支持营销、支持市场，所以是生产决定成败。</span></span></p><p><span style="font-family:宋体;font-size:13px;">7.&nbsp;</span><span style="font-family:宋体;font-size:13px;">体现“一类人才用人才，二类人才用干部，三类人才自己干”的人才观的真实效果。</span></p><p><span style="font-family:宋体;font-size:13px;">8.&nbsp;</span><span style="font-family:宋体;font-size:13px;">破解世界性企业难题：人才、资金。</span></p><p><span style="font-family:宋体;font-size:13px;">9.&nbsp;</span><span style="font-family:宋体;font-size:13px;">长江后浪推前浪，青出于蓝胜于蓝。要发现年轻人，培养年轻人，敢于信用年轻人。</span></p><p><span style="font-family:宋体;font-size:13px;">10.&nbsp;</span><span style="font-family:宋体;font-size:13px;">强化企业造血功能，保持企业活力。工厂造就营销团队，营销团队提升工厂，工厂吸收、培养、输送更优秀的营销人才，形成了一个良性循环。</span></p><p>&nbsp;</p><p><span style="font-family:宋体;font-size:13px;">武汉工厂战略、玉琼团队战略良性互动，也就是工厂出团队，团队提升工厂，1加1远远大于2；使企业活力、造血功能源源不绝。可以说，两大战略的力量是无穷大的。</span></p><p>&nbsp;</p><p style="line-height:29px;"><strong><span style="color:black;font-family:宋体;font-size:13px;">全面创新升级战略的意义</span></strong></p><p style="text-indent:33px;"><span style="font-family:宋体;font-size:13px;">全面创新升级中，最重要的就是思想观念的创新升级。香港会议上（盛隆电气集团创新升级拓展研讨会），凤凰卫视资讯台副台长，著名主持人吴小莉对话主题内容，基本是谈我们的“昨天”、“今天”和“明天”。</span></p><p style="text-indent:20px;"><span style="font-family:宋体;font-size:13px;"></span><span style="font-family:宋体;font-size:13px;">“昨天”大概是包含前20年，我们主要做的是“<strong>扎根养力”</strong>的工作。我们这个企业根扎的很深。根深，就能吸养分﹑吸水分，培育力量，抵制各种侵害，健康地生长发育。“今天”，也就是近十年，简单地说是“<strong>破关聚力</strong>”。即突破体制、机制、人才、资金等瓶颈、关口，把各方面的资源、能量挖掘出来，发挥出来，聚集起来。“明天”，即未来的发展是“<strong>能信发力</strong>”。</span></p><p>&nbsp;</p>','2014-01-12 15:08:49',1389510526,1389510526);
insert into app_pages(menu_id,menu_code,sl_page_id,title,image,content,post_date,post_time,date) values (7,'001004',15,'','','<p style="text-align:justify;-ms-text-justify:inter-ideograph;margin-bottom:10px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">2013</span></strong><strong><span style="color:red;font-family:宋体;font-size:13px;">年</span></strong></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;"><span style="font-family:wingdings;font-size:13px;"><span style="font-family:宋体;font-size:12px;"><span style="font-family:wingdings;font-size:13px;"><span style="font-family:宋体;font-size:12px;"><span style="font-family:wingdings;font-size:13px;"><span style="font-family:wingdings;font-size:13px;"><span style="font-family:宋体;font-size:12px;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2013</span><span style="font-size:13px;">年9月18日，<span style="font-family:宋体;font-size:12px;"><span style="font-family:宋体;font-size:12px;">盛隆电气集团武汉公司荣获2013武汉企业100强</span></span>。</span></span></span></span></span></span></span></span></span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;"><span style="font-family:wingdings;font-size:13px;"><span style="font-family:宋体;font-size:12px;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2013</span><span style="font-size:13px;">年9月9日，<span style="font-family:宋体;font-size:12px;">盛隆电气集团荣获“亚洲品牌500强”</span>。</span></span></span></span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2013</span><span style="font-size:13px;">年9月3日，<span style="font-family:宋体;font-size:12px;">盛隆电气集团入选工信部和中电联联合确定的&nbsp;《全国工业领域电力需求侧管理服务机构名单（第一批）》。盛隆电气在49家中排名第8，是全国主流盘厂中惟一一家。</span></span></span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2013</span><span style="font-size:13px;">年3月21日，盛隆电气集团武汉公司荣获2012度武汉市优秀企业。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2013</span><span style="font-size:13px;">年3月18日，谢元德董事长荣获2012湖北民营经济年度领军人物。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2013</span><span style="font-size:13px;">年2月2日，盛隆电气集团感恩答谢年欢会在人民大会堂金色大厅隆重举行，答谢晚宴在人民大会堂宴会厅举行。本场盛会由央视财经频道著名主持人陈伟鸿，王小丫担任主持，著名歌唱家宋祖英，戴玉强，白雪等多位艺术家倾情加盟，为大家呈现了一场精彩纷呈的文艺演出。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2013</span><span style="font-size:13px;">年1月31日，谢元德董事长荣膺2012武汉民营经济年度最具影响力企业家。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2013</span><span style="font-size:13px;">年1月28日，谢元德董事长荣获2012湖北经济年度风云人物。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2013</span><span style="font-size:13px;">年1月19日，谢元德董事长荣获2012湖北省企业精神文明建设最佳杰出贡献人物。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2013</span><span style="font-size:13px;">年1月14日，谢元德董事长荣获2012中国经济十大商业领袖。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2013</span><span style="font-size:13px;">年1月，盛隆电气集团武汉公司荣获2012第二届中国光谷体育文化节先进单位。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;">&nbsp;</p><p style="margin-bottom:10px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">2012</span></strong><strong><span style="color:red;font-family:宋体;font-size:13px;">年</span></strong></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2012</span><span style="font-size:13px;">年12月10日，盛隆电气集团2012全国三委会议在武汉隆重召开。谢元德董事长在会上发表题为</span>“<span style="font-size:13px;">让一度电创造更多</span>GDP<span style="font-size:13px;">，让电使幸福生活更幸福，让电使美丽中国更美丽</span>”<span style="font-size:13px;">的主旨讲话，深入贯彻学习党的十八大会议精神，紧密联系公司实际，提出了新时期的新口号，号召全体盛隆人抓住宝贵机遇，实现盛隆的突破式跨越式发展。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2012</span><span style="font-size:13px;">年10月13，盛隆电气集团武汉公司荣获2012武汉企业100强。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2012</span><span style="font-size:13px;">年9月，谢元德董事长荣获2012“最光谷•时代推动力30人”称号。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2012</span><span style="font-size:13px;">年9月，《盛隆电气集团基本制度文件》的颁行，宣告富有时代特色和盛隆特色的盛隆企业体制、机制、文化已经基本成熟，正式确立，这是我们对中国特色社会主义企业体制创新的一份贡献，是盛隆集团走向以法治企、以德治企的里程碑，确立了“盛隆宪法”，为百年不倒，百年辉煌保驾护航。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2012</span><span style="font-size:13px;">年5月19日，由盛隆电气集团有限公司总冠名，湖北省体育局、东湖新技术开发区联合主办的“盛隆电气第二届中国光谷体育文化节”在湖北奥林匹克体育中心盛大开幕。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2012</span><span style="font-size:13px;">年3月，盛隆电气集团武汉公司荣获2011文化体育工作先进单位。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;">&nbsp;</p><p style="margin-bottom:10px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">2011</span></strong><strong><span style="color:red;font-family:宋体;font-size:13px;">年</span></strong></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2011</span><span style="font-size:13px;">年12月19日，盛隆电气集团创新升级拓展研讨会在香港召开。会议以学习、创新、拓展为主题，邀请了凤凰卫视资讯台副台长，著名主持人吴小莉主持《对话》栏目，通过对盛隆六个嘉宾团的访谈，回顾和展望盛隆的昨天、今天和明天。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2011</span><span style="font-size:13px;">年8月18日，集团公司与ABB公司签署“10KV开关柜合作生产”协议。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2011</span><span style="font-size:13px;">年6月10日，中国光谷。盛隆电气晚会在武汉隆重举行。晚会由武汉东湖新技术开发区管理委员会和盛隆电气集团有限公司联合主办，以建党九十周年为主题，围绕“政企同心建光谷，干群携手创辉煌”、“中国光谷红歌亮，盛隆歌声献给党”的中心思想展开，是政府与企业携手谱写新篇的一个缩影。晚会由中央电视台著名主持人周涛、刘仪伟担任主持，陈慧琳、那英、杨洪基、何祚欢以及总政歌剧团多位艺术家魅力巨献，为现场七千多名观众奉上了一场华丽的视听盛宴。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2011</span><span style="font-size:13px;">年5月，盛隆电气集团武汉公司荣获2010武汉名牌产品。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2011</span><span style="font-size:13px;">年5月，盛隆电气集团武汉公司荣获2011湖北省功勋企业。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2011</span><span style="font-size:13px;">年5月28日，谢元德董事长荣获2011湖北省功勋企业家荣誉称号。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2011</span><span style="font-size:13px;">年4月，集团公司与清华大学签订协议成立“清华大学校友盛隆电气实践基金”。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2011</span><span style="font-size:13px;">年2月22日，盛隆电气集团武汉公司入驻四万平米巨型厂房新园区。</span></span></p><p>&nbsp;</p><p style="margin-bottom:10px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">2010</span></strong><strong><span style="color:red;font-family:宋体;font-size:13px;">年</span></strong></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2010</span><span style="font-size:13px;">年12月18日，集团董事会暨营销队伍建设总结交流会在成渝两地举行。以盛隆文化为指导《建设“双精”领导集体，打造“共赢”营销团队；启动“白总”培养工程》正式启动。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2010</span><span style="font-size:13px;">年6月，北京智能配电研发中心成立。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2010</span><span style="font-size:13px;">年5月2日，盛隆大学北京培训中心挂牌成立。</span></span></p><p>&nbsp;</p><p style="margin-bottom:10px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">2009</span></strong><strong><span style="color:red;font-family:宋体;font-size:13px;">年</span></strong></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2009</span><span style="font-size:13px;">年12月，广州会议讨论并通过了“一体三委”盛隆大纲，树立了盛隆发展史上一块极具重要意义的里程碑。同时，会议还进一步提出文化制度战略，即“学习中国文化，建设盛隆文化，提高盛隆电气集团的品牌价值”进一步丰富了盛隆文化的内涵。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2009</span><span style="font-size:13px;">年8月管理干部培训班上，“促就业、帮创业、十万大军建大业”的人才战略被作为盛隆的一项长期发展战略深入贯彻到盛隆人的心中。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2009</span><span style="font-size:13px;">年5月28日，盛隆电气三十周年庆典大会武汉会议在洪山礼堂隆重举行。中央电视台主持人管彤专程从北京赶来现场担任本次节目的主持人。著名表演艺术家冯巩通过相声《盛隆你好》，给现场的观众朋友们传递了节日的问候。节目最后，著名歌唱家杨洪基现场演唱了《盛隆之歌》。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">2009</span><span style="font-size:13px;">年5月2日，盛隆电气三十周年庆典大会在钓鱼台国宾馆芳菲苑隆重举行。北京十个亿生产基地正式投产。</span></span></p><p>&nbsp;</p><p style="margin-bottom:10px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">2008</span></strong><strong><span style="color:red;font-family:宋体;font-size:13px;">年</span></strong></p><p><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">盛隆电气扎营重庆。</span></span></p><p>&nbsp;</p><p style="margin-bottom:10px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">2007</span></strong><strong><span style="color:red;font-family:宋体;font-size:13px;">年</span></strong></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">上海战略研讨会讨论并决定了“五营三角连环阵的市场战略”，正式拉开“分兵七路扎据点，百员大将抢外滩”的序幕。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">盛隆电气进驻上海。</span></span></p><p>&nbsp;</p><p style="margin-bottom:10px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">2004</span></strong><strong><span style="color:red;font-family:宋体;font-size:13px;">年</span></strong></p><p><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">盛隆电气进驻东莞。</span></span></p><p>&nbsp;</p><p style="margin-bottom:10px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">2002</span></strong><strong><span style="color:red;font-family:宋体;font-size:13px;">年</span></strong></p><p><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">谢元德董事长当选湖北省第十届人大代表。</span></span></p><p>&nbsp;</p><p style="margin-bottom:10px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">1999</span></strong><strong><span style="color:red;font-family:宋体;font-size:13px;">年</span></strong></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">1999</span><span style="font-size:13px;">年5月13日，盛隆电气二十周年之际，在北京人民大会堂召开新闻发布会，标志着盛隆电气集团正式进驻北京。</span></span></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">1999</span><span style="font-size:13px;">年5月13日，</span><span style="font-size:12px;">在北京人民大会堂成立盛隆电气北京研究所，中国工程院院士、国家著名电力专家顾国彪院士担任研究所所长。</span></span></p><p>&nbsp;</p><p style="margin-bottom:10px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">1997</span></strong><strong><span style="color:red;font-family:宋体;font-size:13px;">年</span></strong></p><p><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">谢元德董事长当选湖北省第九届人大代表。</span></span></p><p>&nbsp;</p><p style="margin-bottom:10px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">1994</span></strong><strong><span style="color:red;font-family:宋体;font-size:13px;">年</span></strong></p><p><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">1994</span><span style="font-size:13px;">年5月13日，盛隆电气十五周年之际，在湖北省政治文化中心洪山大礼堂召开新闻发布会，并在武汉关东科技工业园举行了武汉工厂投产仪式。湖北省副省长韩南鹏到场讲话，著名教育家、原武汉大学校长刘道玉、武汉市常务副市长袁善腊为本次投产仪式剪彩，标志着盛隆电气集团正式进驻武汉，在关东科技工业园落户。</span></span></p><p style="text-indent:27px;">&nbsp;</p><p style="margin-bottom:10px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">1992</span></strong><strong><span style="color:red;font-family:宋体;font-size:13px;">年</span></strong></p><p><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">谢元德董事长当选湖北省第八届人大代表。</span></span></p><p>&nbsp;</p><p style="margin-bottom:10px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">1988</span></strong><strong><span style="color:red;font-family:宋体;font-size:13px;">年</span></strong></p><p><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">谢元德董事长荣获湖北省企业家荣誉称号。</span></span></p><p style="margin-bottom:10px;">&nbsp;</p><p style="margin-bottom:10px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">1987</span></strong><strong><span style="color:red;font-family:宋体;font-size:13px;">年</span></strong></p><p><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">谢元德董事长被评为襄阳（襄樊）市委优秀共产党员、枣阳市委优秀共产党员。</span></span></p><p style="margin-bottom:10px;">&nbsp;</p><p style="margin-bottom:10px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">1986</span></strong><strong><span style="color:red;font-family:宋体;font-size:13px;">年</span></strong></p><p><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">谢元德董事长被评为湖北省劳动模范。</span></span></p><p style="margin-bottom:10px;">&nbsp;</p><p style="margin-bottom:10px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">1985</span></strong><strong><span style="color:red;font-family:宋体;font-size:13px;">年</span></strong></p><p><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">谢元德董事长被中共湖北省评选为优秀共产党员。</span></span></p><p style="margin-bottom:10px;">&nbsp;</p><p style="margin-bottom:10px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">1984</span></strong><strong><span style="color:red;font-family:宋体;font-size:13px;">年</span></strong></p><p><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">与交交通部长江航运管理局合资创办鄂北电气遥控设备厂（后更名为枣阳市开关厂）。</span></span></p><p><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">谢元德董事长当选襄阳（襄樊）市第六次党代会党代表。</span></span></p><p style="margin-bottom:10px;">&nbsp;</p><p style="margin-bottom:10px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">1983</span></strong><strong><span style="color:red;font-family:宋体;font-size:13px;">年</span></strong></p><p><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">谢元德董事长当选枣阳市人大代表、政协委员。</span></span></p><p>&nbsp;</p><p style="margin-bottom:10px;"><strong><span style="color:red;font-family:宋体;font-size:13px;">1979</span></strong><strong><span style="color:red;font-family:宋体;font-size:13px;">年</span></strong></p><p style="text-align:justify;-ms-text-justify:inter-ideograph;"><span style="font-family:wingdings;font-size:13px;">l</span><span style="font-family:宋体;"><span style="font-size:13px;">1979</span><span style="font-size:13px;">年5月13日，513公司（盛隆电气前身）。在湖北省枣阳市成立，以“为四化建设出力，为人民造福流汗”作为其奋斗的宗旨。</span></span></p><p>&nbsp;</p><p>&nbsp;</p><p><span style="font-family:;">‍</span></p><p>&nbsp;</p>','2014-01-12 15:08:50',1389510526,1389510526);
