insert  into `app_news`(`id`,`sl_cid`,`sl_news_id`,`title`,`post_date`,`post_time`,`year`,`image`,`content`,`date`,`sl_url`,`icon`,`post_desc`,`page`) values (1,7,149,'合作共赢——施耐德全球财务总裁访问盛隆电气','2013-10-15',1381766400,0,'http://m.shenglong-electric.com.cn/uploads/2013/10/3_64_64.64_64.jpg','<p style=\"margin:0cm 0cm 0px\" class=\"MsoNormal\"><span style=\"font-family:times new roman\" lang=\"EN-US\"><span style=\"font-family:宋体, simsun;font-size:12px\">2013</span></span><span style=\"font-family:宋体, simsun;font-size:12px;mso-ascii-font-family:&#39;times new roman&#39;;mso-hansi-font-family:&#39;times new roman&#39;\">年</span><span style=\"font-family:times new roman\" lang=\"EN-US\"><span style=\"font-family:宋体, simsun;font-size:12px\">10</span></span><span style=\"font-family:宋体, simsun;font-size:12px;mso-ascii-font-family:&#39;times new roman&#39;;mso-hansi-font-family:&#39;times new roman&#39;\">月</span><span style=\"font-family:times new roman\" lang=\"EN-US\"><span style=\"font-family:宋体, simsun;font-size:12px\">15</span></span><span style=\"font-family:宋体;mso-ascii-font-family:&#39;times new roman&#39;;mso-hansi-font-family:&#39;times new roman&#39;\"><span style=\"font-family:宋体, simsun;font-size:12px;mso-ascii-font-family:&#39;times new roman&#39;;mso-hansi-font-family:&#39;times new roman&#39;\">日，施耐德全球财务总裁、中国财务总裁一行访问盛隆电气</span><span style=\"font-family:宋体, simsun;font-size:12px;mso-ascii-font-family:&#39;times new roman&#39;;mso-hansi-font-family:&#39;times new roman&#39;\">。</span></span></p><p style=\"margin:0cm 0cm 0px\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-font-family:&#39;times new roman&#39;;mso-hansi-font-family:&#39;times new roman&#39;\"><span style=\"font-family:宋体, simsun;font-size:12px;mso-ascii-font-family:&#39;times new roman&#39;;mso-hansi-font-family:&#39;times new roman&#39;\"></span></span>&nbsp;</p><p style=\"text-align:center;margin:0cm 0cm 0px\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-font-family:&