/*Table structure for table `app_pages` */

DROP TABLE IF EXISTS `app_pages`;

CREATE TABLE `app_pages` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `sl_page_id` int(10) NOT NULL DEFAULT '0' COMMENT 'sl_page_id',
  `title` varchar(255) NOT NULL DEFAULT '' COMMENT 'title',
  `image` varchar(255) NOT NULL DEFAULT '' COMMENT 'image',
  `content` text,
  `post_date` varchar(60) NOT NULL DEFAULT '' COMMENT 'post_date',
  `post_time` int(10) NOT NULL DEFAULT '0' COMMENT 'post_time',
  `date` int(10) NOT NULL DEFAULT '0' COMMENT 'date',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=35 DEFAULT CHARSET=utf8;

/*Data for the table `app_pages` */

insert  into `app_pages`(`id`,`sl_page_id`,`title`,`image`,`content`,`post_date`,`post_time`,`date`) values (1,13,'','','<p style=\"line-height:1.5em;text-align:justify;margin-bottom:10px;\"><span style=\"font-size:13px;color:#383838;font-family:宋体\">&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"font-size:13px;color:#383838;font-family:宋体,simsun\">盛隆电气集团成立于1979年，是中国领先的提供输配电设备、系统及解决方案的厂商。盛隆电气集团业务遍布中国所有省市及海外20多个国家，拥有4000多名员工，是北京、华中、华北市场成套输配电设备领域的引领厂商。</span></span></p><p style=\"line-height:1.5em;text-align:left;margin-top:auto;margin-bottom:10px;\"><span style=\"font-size:13px;color:#383838;font-family:宋体,simsun\">&nbsp;&nbsp;&nbsp;&nbsp;盛隆电气集团下属12个子公司，5个工厂，1个研究所，20多个分公司或办事处，其中北京公司和武汉公司分别在华北和华中市场位居引领地位。武汉公司当选为2012年武汉百强企业。集团在北京、武汉、上海、东莞和重庆建有大型生产工厂。</span></p><p style=\"line-height:1.5em;text-align:left;margin-top:auto;margin-bottom:10px;\"><span style=\"font-size:13px;color:#383838;font-family:宋体,simsun\">&nbsp;&nbsp;&nbsp;&nbsp;盛隆电气集团在成套输配电设备、电力监控、电力智能、电力节能四大领域有广泛的产品线和解决方案，这些产品和解决方案致力于使客户发电和使用电力更加安全、可靠、节能和环保。面对日益增长的能源需求和节能减排的严峻挑战，盛隆电气集团通过先进的产品和解决方案，帮助客户提高电力使用效率，降低电力能耗，减少碳排放量，创造良好的社会、经济和环境效益。盛隆电气集团的产品已广泛应用于基础设施、能源、工业、商业、楼宇、民用住宅等市场，如国家电厂、铁路、高速公路、地铁、机场、奥运场馆等国家重点工程。</span></p><p style=\"line-height:1.5em;text-align:left;margin-top:auto;margin-bottom:10px;\"><span style=\"font-size:13px;color:#383838;font-family:宋体,simsun\">&nbsp;&nbsp;&nbsp;&nbsp;盛隆电气集团一贯重视科技研发以及自主创新，在电力智能及电力节能领域获得了多项国家专利和软件著作权。集团长期坚持走产学研一体化道路，与清华大学、北京大学、中科院电工研究所等单位有20多年的良好合作关系。1999年集团公司在北京人民大会堂成立盛隆电气北京研究所，中国工程院院士、国家著名电力专家顾国彪院士担任研究所所长。集团公司在立足于自主创新的基础上，在技术领域与知名跨国公司如ABB、西门子、施耐德等也有着良好的合作关系。&nbsp;</span></p><p style=\"line-height:1.5em;text-align:left\"><span style=\"font-size:13px;color:#383838;font-family:宋体,simsun\">&nbsp;&nbsp;&nbsp;&nbsp;盛隆电气集团坚持以人为本，高度重视引进人才、培养人才、留住人才，于2010年建立了盛隆大学北京大型培训基地。盛隆电气集团用先进的技术为客户提供产品的同时，也积极承担社会责任，得到了社会的广泛认可。</span></p><p style=\"line-height:1.5em;text-align:left\"><br /></p><p style=\"line-height:22px;text-align:left\"><span style=\"color:#383838;font-family:\"></span></p><p style=\"line-height:1.5em;text-align:justify\"><span style=\"font-size:13px;font-family:宋体,simsun\"></span></p><p style=\"line-height:1.5em;text-align:justify\"><span style=\"font-size:13px;font-family:宋体,simsun\"></span></p>','2014-01-10 17:59:52',1389347991,1389347991),(2,14,'','','<p style=\"line-height:1.5em;\"><span style=\"font-family:宋体;font-size:13px;\">&nbsp;&nbsp;&nbsp;&nbsp;盛隆电气集团于1985年成立党支部，2008年建立党委，下设5个党支部。近年来，盛隆电气集团一直以学习型党支部建设为基础，确立党组织的政治核心地位，发挥党员在党组织建设和集团建设中的主体作用，积极探索党组织建设的新模式，新方法。盛隆秉承“为四化建设出力，为人民造福流汗”的宗旨，以党组织的凝聚力和号召力，激发党支部和党员参与企业决策以及经营管理的积极性和创造性。真正做到企业决策有党的声音，企业管理有党的身影。一直以来，集团党支部加强员工的思想政治工作，健全完善学习型党组织建设的制度体系，努力打造具有盛隆特色的党组织文化，倾力建设活力和谐的盛隆电气集团。</span></p><p>&nbsp;</p>','2014-01-10 17:59:53',1389347991,1389347991),(3,30,'','','<p><strong><span style=\"font-size:13px;font-family:宋体;color:red\">一个目标：百年辉煌</span></strong></p><p>&nbsp;</p><p><span style=\"font-size:13px;font-family:宋体\">2009</span><span style=\"font-size:13px;font-family:宋体\">年5月2日，<span style=\"color:#383838\">盛隆电气创建三十周年庆典大会在北京钓鱼台国宾馆隆重举行。会上，谢元德</span>董事长<span style=\"color:#383838\">提出了“诚信攀越百年峰，百年辉煌我盛隆”的战略目标。</span></span></p><p>&nbsp;</p><p><span style=\"font-size:13px;font-family:宋体\">2004</span><span style=\"font-size:13px;font-family:宋体\">年11月13日，谢元德董事长在“盛隆电气百年不倒武汉定位研讨会”上提出了盛隆电气百年不倒的总目标。</span></p><p>&nbsp;</p><p style=\"text-align:left;line-height:normal\"><span style=\"font-size:13px;font-family:宋体;color:#1f497d\">“盛隆电气百年不倒是我最大的需要。</span></p><p style=\"text-align:left;line-height:normal\"><span style=\"font-size:13px;font-family:宋体;color:#1f497d\">有技术的传授技术，有经验的传授经验，这是我最大的希望。</span></p><p style=\"text-align:left;line-height:normal\"><span style=\"font-size:13px;font-family:宋体;color:#1f497d\">年轻的朋友们，快成长，快成长，快快成长，这是我长久的期盼。</span></p><p style=\"text-align:left;margin:0px;line-height:normal\"><span style=\"font-size:13px;font-family:宋体;color:#1f497d\">互敬互爱，相互关怀，大家开心，人人发财，看到你们一代一代，事业有成，夫妻恩爱，这是我最大的心愿！”</span></p><p style=\"text-align:right\"><strong><span style=\"font-size:13px;font-family:宋体\">摘自&nbsp;谢元德董事长2004年《说句心里话》</span></strong></p><p>&nbsp;</p><p>&nbsp;</p><p><strong><span style=\"font-size:13px;font-family:宋体;color:red\">三个战略：“五营三角连环阵”市场战略、“十万大军”人才战略、文化制度战略</span></strong></p><p>&nbsp;</p><p><img title=\"map\" style=\"height:350px;float:right;width:350px\" border=\"0\" hspace=\"0\" src=\"http://m.shenglong-electric.com.cn/uploads/20130326/13642794016969.jpg\" width=\"350\" height=\"350\" /></p><p><strong><span style=\"font-size:13px;font-family:宋体;color:red\">“五营三角连环阵”市场战略</span></strong></p><p>&nbsp;</p><p><strong><span style=\"font-size:13px;font-family:宋体\">第一战役（2008年）</span></strong></p><p><span style=\"font-size:13px;font-family:宋体\">分兵七路扎据点，百员大将抢外滩</span></p><p><strong><span style=\"font-size:13px;font-family:宋体\">第二战役（2009年）</span></strong></p><p><span style=\"font-size:13px;font-family:宋体\">〇九三角大汇演，北东中部大营连</span></p><p><strong><span style=\"font-size:13px;font-family:宋体\">第三战役（2010年）</span></strong></p><p><span style=\"font-size:13px;font-family:宋体\">京汉破竹连珠响，扎营重庆战东莞</span></p><p><strong><span style=\"font-size:13px;font-family:宋体\">第四战役（2011年）</span></strong></p><p><span style=\"font-size:13px;font-family:宋体\">实现省会全覆盖，五营三角核心圈</span></p><p><strong><span style=\"font-size:13px;font-family:宋体\">第五战役（2012年</span></strong><span style=\"font-size:13px;font-family:宋体\">）</span></p><p><span style=\"font-size:13px;font-family:宋体\">打牢国内百年基，迈出国门试深浅</span></p><p>&nbsp;</p><p><strong><span style=\"font-size:13px;font-family:宋体;color:red\">“十万大军”人才战略</span></strong></p><p>&nbsp;</p><p><strong><span style=\"font-size:13px;font-family:宋体;letter-spacing:0px\">盛隆电气集团组织、人事、管理制度（一号制度）</span></strong><strong><span style=\"font-size:13px;font-family:宋体\">第二条&nbsp;：</span></strong><span style=\"font-size:13px;font-family:宋体\"></span></p><p><span style=\"font-size:13px;font-family:宋体;letter-spacing:0px\">关心、支持、帮助就业、创业是盛隆企业文化的核心。盛隆电气是一个创业平台，千方百计帮助每一名盛隆员工成才、创业、做大、做强。</span></p><p>&nbsp;</p><p><span style=\"font-size:13px;font-family:宋体;color:#1f497d\">“</span><span style=\"font-size:13px;font-family:宋体;color:#1f497d\">我了解现在的年轻人生活压力很大，我们公司年轻人多，在当下这个大环境，都很不容易。我们既然有这个企业，大家信任这个企业，进了这个企业，这个企业一定要负责任，对这些年轻人负责任。作为一个企业的负责人，特别需要重视年轻人的现实问题，根据大家的切身需要去思考。年轻人有需要、有干劲，有很大的积极性，那么怎么去引导大家合理的需要，怎么去激励大家的干劲，怎么找寻一些办法，一起去创造、去发展？在企业发展的每一阶段，必须明确、树立前进的方向，奋斗的目标。如何确定奋斗的方向和目标？一是要把个人的目标与企业的目标很好地统一起来。企业的目标是为满足员工的目标而设的。二是目标不能太虚，但是一定要积极。当然也要稳妥...”</span></p><p style=\"text-align:right\"><strong><span style=\"font-size:13px;font-family:宋体\">董事长2012年1月16日在武汉工厂战略总动员会议上的讲话（摘要）</span></strong></p><p style=\"text-align:right\">&nbsp;</p><p><span style=\"font-size:13px;font-family:宋体;color:#1f497d\">“我始终不同意当一辈子工人的观念，应当大大拓宽他们的职业发展道路。给他们更广阔的成长空间，更多的发展机遇与道路选择。使他们的个人前途更加宽广更有保障。这是最切实的帮助，最根本的关心，是真正的以人为本，对青年的成长和人生真正负责。当然，这也是为公司培养优秀人才，增加宝贵财富。事实证明，年青人经过一段时间生产车间艰苦劳动的锻炼磨砺，不仅学习了生产技术，掌握了一些专业技能，而且更是对自己思想、品性的涵养提高、多方面素质的培养修成，发挥了熔炉作用。对他们今后长期的人生发展十分有利，打下了一个很好的根基。公司内外，普遍称赞这些经过车间劳动锻炼，转行做营销的年青人，有梦想，敢追求，能吃苦，懂技术，重诚信。他们能走向成功，绝非偶然，应当感谢车间劳动给他们的锻炼和磨砺。</span></p><p><span style=\"color:#7f7f7f\"></span></p><p><span style=\"font-size:13px;font-family:宋体;color:#1f497d\">正是基于这些成功的经验，我们认为，一线的生产车间，不仅可以出电气产品，更能出各种人才。我们完全可以通过努力，把一线生产厂变成一所半工半读式的大学校。如果实施的好，将为盛隆年青人更好、更快地成长、成才、成功，打开方便之门，铺设一条通向光明未来的快车道。同时成批量、成规模地、源源不断地为集团输送优秀人才。这对公司的长远发展，是具有战略意义的。”</span></p><p style=\"text-align:right;line-height:150%\"><strong><span style=\"font-size:13px;font-family:宋体;letter-spacing:0px;line-height:150%\">董事长2010年北京庆功会讲话</span></strong><strong><span style=\"font-size:13px;font-family:宋体;line-height:150%\">（摘要）</span></strong></p><p style=\"text-align:right\">&nbsp;</p><p style=\"text-align:center\"><img title=\"recai2\" style=\"height:350px;width:350px\" border=\"0\" hspace=\"0\" src=\"http://m.shenglong-electric.com.cn/uploads/20130326/13642710701022.jpg\" width=\"350\" height=\"350\" /></p><p><strong><span style=\"font-size:13px;font-family:宋体;color:red\">文化制度战略</span></strong></p><p>&nbsp;</p><p><span style=\"font-size:13px;font-family:宋体\">盛隆文化是中国优秀民族文化的继承，发扬、具体化。我们重视从中国民族传统文化中汲取精华，建设盛隆文化。早期：为四化建设，为人民造福；九十年代：盛世同襄盛举，隆情共创隆图；盛隆企业文化规定的：表层文化：温暖和谐，昂扬向上；中层文化：科学合理，易知易行；深层文化：追求卓越，奉献社会；我们一直提倡贯彻为国为民，敢想敢干，以人为本，科学发展。这些都是盛隆文化的集中表现，体现了盛隆实际与中国优秀民族传统文化的结合。</span></p><p>&nbsp;</p><p><span style=\"font-size:13px;font-family:宋体\">（请了解公司积淀30年&nbsp;十大代表性文化）</span></p><p>&nbsp;</p>','2014-01-10 17:59:53',1389347991,1389347991),(4,31,'','','<p style=\"line-height:19px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">中期目标：三大战略给力200亿</span></strong></p><p style=\"line-height:19px;\">&nbsp;</p><p><strong><span style=\"color:black;font-family:宋体;font-size:13px;\">武汉工厂战略的内容和意义</span></strong></p><p style=\"line-height:29px;\"><span style=\"color:black;font-family:宋体;font-size:13px;\">1.</span><span style=\"color:black;font-family:宋体;font-size:13px;\">建设中国电气开关行业现代化的第一车间。</span></p><p><span style=\"font-family:宋体;font-size:13px;\">2.</span><span style=\"font-family:宋体;font-size:13px;\">打造盛隆大学培养现代企业人才第一课堂。</span></p><p style=\"line-height:29px;\">&nbsp;</p><p style=\"line-height:29px;\"><strong><span style=\"color:black;font-family:宋体;font-size:13px;\">玉琼团队战略的意义、特点和价值</span></strong></p><p><span style=\"font-family:宋体;font-size:13px;\">1.</span><span style=\"font-family:宋体;font-size:13px;\">经营方式的转型升级：由资金投入式转为“能信营创式”</span></p><p><span style=\"font-family:宋体;font-size:13px;\">2.</span><span style=\"font-family:宋体;font-size:13px;\">团队的组织计划性、市场定位的准确性、营销模式与对象的战略性、队伍发展的可持续性。</span></p><p><span style=\"font-family:宋体;font-size:13px;\">3.</span><span style=\"font-family:宋体;font-size:13px;\">体现重庆会议团队建设目标。以盛隆文化为指导，建设“双精”领导集体，打造“共赢”营销团队，启动“百总”培养工程。打造永不满足现状、勇于学习、善于学习，能够不断充实、提高自己，不断向新的奋斗目标攀登进取的团队。</span></p><p><span style=\"font-family:宋体;font-size:13px;\">4.</span><span style=\"font-family:宋体;font-size:13px;\">体现出生产管理工作的价值，工厂培养人才，成就高级人才。</span></p><p><span style=\"font-family:宋体;font-size:13px;\">5.</span><span style=\"font-family:宋体;font-size:13px;\">体现公司文化：个人主义与集体主义的有机结合。</span></p><p><span style=\"font-family:宋体;font-size:13px;\">6.</span><span style=\"font-family:宋体;font-size:13px;\">营销引导生产，生产决定成败。<span style=\"color:black;\">能信营创的团队，对自己的生产搭档，在如何创新、提高，适销对路，满足市场、顾客方面，有引导的作用，有引导的能力。由此体现到生产决定成败。没有生产做好产品，控制成本，提供好的服务，不可能支持营销、支持市场，所以是生产决定成败。</span></span></p><p><span style=\"font-family:宋体;font-size:13px;\">7.&nbsp;</span><span style=\"font-family:宋体;font-size:13px;\">体现“一类人才用人才，二类人才用干部，三类人才自己干”的人才观的真实效果。</span></p><p><span style=\"font-family:宋体;font-size:13px;\">8.&nbsp;</span><span style=\"font-family:宋体;font-size:13px;\">破解世界性企业难题：人才、资金。</span></p><p><span style=\"font-family:宋体;font-size:13px;\">9.&nbsp;</span><span style=\"font-family:宋体;font-size:13px;\">长江后浪推前浪，青出于蓝胜于蓝。要发现年轻人，培养年轻人，敢于信用年轻人。</span></p><p><span style=\"font-family:宋体;font-size:13px;\">10.&nbsp;</span><span style=\"font-family:宋体;font-size:13px;\">强化企业造血功能，保持企业活力。工厂造就营销团队，营销团队提升工厂，工厂吸收、培养、输送更优秀的营销人才，形成了一个良性循环。</span></p><p>&nbsp;</p><p><span style=\"font-family:宋体;font-size:13px;\">武汉工厂战略、玉琼团队战略良性互动，也就是工厂出团队，团队提升工厂，1加1远远大于2；使企业活力、造血功能源源不绝。可以说，两大战略的力量是无穷大的。</span></p><p>&nbsp;</p><p style=\"line-height:29px;\"><strong><span style=\"color:black;font-family:宋体;font-size:13px;\">全面创新升级战略的意义</span></strong></p><p style=\"text-indent:33px;\"><span style=\"font-family:宋体;font-size:13px;\">全面创新升级中，最重要的就是思想观念的创新升级。香港会议上（盛隆电气集团创新升级拓展研讨会），凤凰卫视资讯台副台长，著名主持人吴小莉对话主题内容，基本是谈我们的“昨天”、“今天”和“明天”。</span></p><p style=\"text-indent:20px;\"><span style=\"font-family:宋体;font-size:13px;\"></span><span style=\"font-family:宋体;font-size:13px;\">“昨天”大概是包含前20年，我们主要做的是“<strong>扎根养力”</strong>的工作。我们这个企业根扎的很深。根深，就能吸养分﹑吸水分，培育力量，抵制各种侵害，健康地生长发育。“今天”，也就是近十年，简单地说是“<strong>破关聚力</strong>”。即突破体制、机制、人才、资金等瓶颈、关口，把各方面的资源、能量挖掘出来，发挥出来，聚集起来。“明天”，即未来的发展是“<strong>能信发力</strong>”。</span></p><p>&nbsp;</p>','2014-01-10 17:59:54',1389347991,1389347991),(5,15,'','','<p style=\"text-align:justify;-ms-text-justify:inter-ideograph;margin-bottom:10px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">2013</span></strong><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">年</span></strong></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\"><span style=\"font-family:wingdings;font-size:13px;\"><span style=\"font-family:宋体;font-size:12px;\"><span style=\"font-family:wingdings;font-size:13px;\"><span style=\"font-family:宋体;font-size:12px;\"><span style=\"font-family:wingdings;font-size:13px;\"><span style=\"font-family:wingdings;font-size:13px;\"><span style=\"font-family:宋体;font-size:12px;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2013</span><span style=\"font-size:13px;\">年9月18日，<span style=\"font-family:宋体;font-size:12px;\"><span style=\"font-family:宋体;font-size:12px;\">盛隆电气集团武汉公司荣获2013武汉企业100强</span></span>。</span></span></span></span></span></span></span></span></span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\"><span style=\"font-family:wingdings;font-size:13px;\"><span style=\"font-family:宋体;font-size:12px;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2013</span><span style=\"font-size:13px;\">年9月9日，<span style=\"font-family:宋体;font-size:12px;\">盛隆电气集团荣获“亚洲品牌500强”</span>。</span></span></span></span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2013</span><span style=\"font-size:13px;\">年9月3日，<span style=\"font-family:宋体;font-size:12px;\">盛隆电气集团入选工信部和中电联联合确定的&nbsp;《全国工业领域电力需求侧管理服务机构名单（第一批）》。盛隆电气在49家中排名第8，是全国主流盘厂中惟一一家。</span></span></span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2013</span><span style=\"font-size:13px;\">年3月21日，盛隆电气集团武汉公司荣获2012度武汉市优秀企业。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2013</span><span style=\"font-size:13px;\">年3月18日，谢元德董事长荣获2012湖北民营经济年度领军人物。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2013</span><span style=\"font-size:13px;\">年2月2日，盛隆电气集团感恩答谢年欢会在人民大会堂金色大厅隆重举行，答谢晚宴在人民大会堂宴会厅举行。本场盛会由央视财经频道著名主持人陈伟鸿，王小丫担任主持，著名歌唱家宋祖英，戴玉强，白雪等多位艺术家倾情加盟，为大家呈现了一场精彩纷呈的文艺演出。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2013</span><span style=\"font-size:13px;\">年1月31日，谢元德董事长荣膺2012武汉民营经济年度最具影响力企业家。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2013</span><span style=\"font-size:13px;\">年1月28日，谢元德董事长荣获2012湖北经济年度风云人物。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2013</span><span style=\"font-size:13px;\">年1月19日，谢元德董事长荣获2012湖北省企业精神文明建设最佳杰出贡献人物。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2013</span><span style=\"font-size:13px;\">年1月14日，谢元德董事长荣获2012中国经济十大商业领袖。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2013</span><span style=\"font-size:13px;\">年1月，盛隆电气集团武汉公司荣获2012第二届中国光谷体育文化节先进单位。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\">&nbsp;</p><p style=\"margin-bottom:10px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">2012</span></strong><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">年</span></strong></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2012</span><span style=\"font-size:13px;\">年12月10日，盛隆电气集团2012全国三委会议在武汉隆重召开。谢元德董事长在会上发表题为</span>“<span style=\"font-size:13px;\">让一度电创造更多</span>GDP<span style=\"font-size:13px;\">，让电使幸福生活更幸福，让电使美丽中国更美丽</span>”<span style=\"font-size:13px;\">的主旨讲话，深入贯彻学习党的十八大会议精神，紧密联系公司实际，提出了新时期的新口号，号召全体盛隆人抓住宝贵机遇，实现盛隆的突破式跨越式发展。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2012</span><span style=\"font-size:13px;\">年10月13，盛隆电气集团武汉公司荣获2012武汉企业100强。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2012</span><span style=\"font-size:13px;\">年9月，谢元德董事长荣获2012“最光谷•时代推动力30人”称号。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2012</span><span style=\"font-size:13px;\">年9月，《盛隆电气集团基本制度文件》的颁行，宣告富有时代特色和盛隆特色的盛隆企业体制、机制、文化已经基本成熟，正式确立，这是我们对中国特色社会主义企业体制创新的一份贡献，是盛隆集团走向以法治企、以德治企的里程碑，确立了“盛隆宪法”，为百年不倒，百年辉煌保驾护航。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2012</span><span style=\"font-size:13px;\">年5月19日，由盛隆电气集团有限公司总冠名，湖北省体育局、东湖新技术开发区联合主办的“盛隆电气第二届中国光谷体育文化节”在湖北奥林匹克体育中心盛大开幕。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2012</span><span style=\"font-size:13px;\">年3月，盛隆电气集团武汉公司荣获2011文化体育工作先进单位。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\">&nbsp;</p><p style=\"margin-bottom:10px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">2011</span></strong><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">年</span></strong></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2011</span><span style=\"font-size:13px;\">年12月19日，盛隆电气集团创新升级拓展研讨会在香港召开。会议以学习、创新、拓展为主题，邀请了凤凰卫视资讯台副台长，著名主持人吴小莉主持《对话》栏目，通过对盛隆六个嘉宾团的访谈，回顾和展望盛隆的昨天、今天和明天。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2011</span><span style=\"font-size:13px;\">年8月18日，集团公司与ABB公司签署“10KV开关柜合作生产”协议。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2011</span><span style=\"font-size:13px;\">年6月10日，中国光谷。盛隆电气晚会在武汉隆重举行。晚会由武汉东湖新技术开发区管理委员会和盛隆电气集团有限公司联合主办，以建党九十周年为主题，围绕“政企同心建光谷，干群携手创辉煌”、“中国光谷红歌亮，盛隆歌声献给党”的中心思想展开，是政府与企业携手谱写新篇的一个缩影。晚会由中央电视台著名主持人周涛、刘仪伟担任主持，陈慧琳、那英、杨洪基、何祚欢以及总政歌剧团多位艺术家魅力巨献，为现场七千多名观众奉上了一场华丽的视听盛宴。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2011</span><span style=\"font-size:13px;\">年5月，盛隆电气集团武汉公司荣获2010武汉名牌产品。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2011</span><span style=\"font-size:13px;\">年5月，盛隆电气集团武汉公司荣获2011湖北省功勋企业。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2011</span><span style=\"font-size:13px;\">年5月28日，谢元德董事长荣获2011湖北省功勋企业家荣誉称号。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2011</span><span style=\"font-size:13px;\">年4月，集团公司与清华大学签订协议成立“清华大学校友盛隆电气实践基金”。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2011</span><span style=\"font-size:13px;\">年2月22日，盛隆电气集团武汉公司入驻四万平米巨型厂房新园区。</span></span></p><p>&nbsp;</p><p style=\"margin-bottom:10px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">2010</span></strong><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">年</span></strong></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2010</span><span style=\"font-size:13px;\">年12月18日，集团董事会暨营销队伍建设总结交流会在成渝两地举行。以盛隆文化为指导《建设“双精”领导集体，打造“共赢”营销团队；启动“白总”培养工程》正式启动。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2010</span><span style=\"font-size:13px;\">年6月，北京智能配电研发中心成立。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2010</span><span style=\"font-size:13px;\">年5月2日，盛隆大学北京培训中心挂牌成立。</span></span></p><p>&nbsp;</p><p style=\"margin-bottom:10px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">2009</span></strong><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">年</span></strong></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2009</span><span style=\"font-size:13px;\">年12月，广州会议讨论并通过了“一体三委”盛隆大纲，树立了盛隆发展史上一块极具重要意义的里程碑。同时，会议还进一步提出文化制度战略，即“学习中国文化，建设盛隆文化，提高盛隆电气集团的品牌价值”进一步丰富了盛隆文化的内涵。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2009</span><span style=\"font-size:13px;\">年8月管理干部培训班上，“促就业、帮创业、十万大军建大业”的人才战略被作为盛隆的一项长期发展战略深入贯彻到盛隆人的心中。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2009</span><span style=\"font-size:13px;\">年5月28日，盛隆电气三十周年庆典大会武汉会议在洪山礼堂隆重举行。中央电视台主持人管彤专程从北京赶来现场担任本次节目的主持人。著名表演艺术家冯巩通过相声《盛隆你好》，给现场的观众朋友们传递了节日的问候。节目最后，著名歌唱家杨洪基现场演唱了《盛隆之歌》。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">2009</span><span style=\"font-size:13px;\">年5月2日，盛隆电气三十周年庆典大会在钓鱼台国宾馆芳菲苑隆重举行。北京十个亿生产基地正式投产。</span></span></p><p>&nbsp;</p><p style=\"margin-bottom:10px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">2008</span></strong><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">年</span></strong></p><p><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">盛隆电气扎营重庆。</span></span></p><p>&nbsp;</p><p style=\"margin-bottom:10px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">2007</span></strong><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">年</span></strong></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">上海战略研讨会讨论并决定了“五营三角连环阵的市场战略”，正式拉开“分兵七路扎据点，百员大将抢外滩”的序幕。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">盛隆电气进驻上海。</span></span></p><p>&nbsp;</p><p style=\"margin-bottom:10px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">2004</span></strong><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">年</span></strong></p><p><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">盛隆电气进驻东莞。</span></span></p><p>&nbsp;</p><p style=\"margin-bottom:10px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">2002</span></strong><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">年</span></strong></p><p><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">谢元德董事长当选湖北省第十届人大代表。</span></span></p><p>&nbsp;</p><p style=\"margin-bottom:10px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">1999</span></strong><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">年</span></strong></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">1999</span><span style=\"font-size:13px;\">年5月13日，盛隆电气二十周年之际，在北京人民大会堂召开新闻发布会，标志着盛隆电气集团正式进驻北京。</span></span></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">1999</span><span style=\"font-size:13px;\">年5月13日，</span><span style=\"font-size:12px;\">在北京人民大会堂成立盛隆电气北京研究所，中国工程院院士、国家著名电力专家顾国彪院士担任研究所所长。</span></span></p><p>&nbsp;</p><p style=\"margin-bottom:10px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">1997</span></strong><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">年</span></strong></p><p><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">谢元德董事长当选湖北省第九届人大代表。</span></span></p><p>&nbsp;</p><p style=\"margin-bottom:10px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">1994</span></strong><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">年</span></strong></p><p><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">1994</span><span style=\"font-size:13px;\">年5月13日，盛隆电气十五周年之际，在湖北省政治文化中心洪山大礼堂召开新闻发布会，并在武汉关东科技工业园举行了武汉工厂投产仪式。湖北省副省长韩南鹏到场讲话，著名教育家、原武汉大学校长刘道玉、武汉市常务副市长袁善腊为本次投产仪式剪彩，标志着盛隆电气集团正式进驻武汉，在关东科技工业园落户。</span></span></p><p style=\"text-indent:27px;\">&nbsp;</p><p style=\"margin-bottom:10px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">1992</span></strong><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">年</span></strong></p><p><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">谢元德董事长当选湖北省第八届人大代表。</span></span></p><p>&nbsp;</p><p style=\"margin-bottom:10px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">1988</span></strong><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">年</span></strong></p><p><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">谢元德董事长荣获湖北省企业家荣誉称号。</span></span></p><p style=\"margin-bottom:10px;\">&nbsp;</p><p style=\"margin-bottom:10px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">1987</span></strong><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">年</span></strong></p><p><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">谢元德董事长被评为襄阳（襄樊）市委优秀共产党员、枣阳市委优秀共产党员。</span></span></p><p style=\"margin-bottom:10px;\">&nbsp;</p><p style=\"margin-bottom:10px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">1986</span></strong><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">年</span></strong></p><p><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">谢元德董事长被评为湖北省劳动模范。</span></span></p><p style=\"margin-bottom:10px;\">&nbsp;</p><p style=\"margin-bottom:10px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">1985</span></strong><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">年</span></strong></p><p><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">谢元德董事长被中共湖北省评选为优秀共产党员。</span></span></p><p style=\"margin-bottom:10px;\">&nbsp;</p><p style=\"margin-bottom:10px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">1984</span></strong><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">年</span></strong></p><p><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">与交交通部长江航运管理局合资创办鄂北电气遥控设备厂（后更名为枣阳市开关厂）。</span></span></p><p><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">谢元德董事长当选襄阳（襄樊）市第六次党代会党代表。</span></span></p><p style=\"margin-bottom:10px;\">&nbsp;</p><p style=\"margin-bottom:10px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">1983</span></strong><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">年</span></strong></p><p><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">谢元德董事长当选枣阳市人大代表、政协委员。</span></span></p><p>&nbsp;</p><p style=\"margin-bottom:10px;\"><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">1979</span></strong><strong><span style=\"color:red;font-family:宋体;font-size:13px;\">年</span></strong></p><p style=\"text-align:justify;-ms-text-justify:inter-ideograph;\"><span style=\"font-family:wingdings;font-size:13px;\">l</span><span style=\"font-family:宋体;\"><span style=\"font-size:13px;\">1979</span><span style=\"font-size:13px;\">年5月13日，513公司（盛隆电气前身）。在湖北省枣阳市成立，以“为四化建设出力，为人民造福流汗”作为其奋斗的宗旨。</span></span></p><p>&nbsp;</p><p>&nbsp;</p><p><span style=\"font-family:;\">‍</span></p><p>&nbsp;</p>','2014-01-10 17:59:55',1389347991,1389347991),(6,16,'','','<p style=\"line-height:24px;background:white;margin-top:5px;margin-bottom:5px;\"><span style=\"font-size:12px\">入选《全国工业领域电力需求侧管理服务机构名单（第一批）》</span></p><p style=\"line-height:24px;background:white;margin-top:5px;margin-bottom:5px;\"><span style=\"font-size:12px\">ISO9001</span><span style=\"font-size:12px\">：2000质量管理体系认证</span></p><p style=\"margin:5px 0;line-height:24px;background-color:white\"><span style=\"font-size:12px\">ISO14001</span><span style=\"font-size:12px\">：2004环境管理体系认证</span></p><p style=\"margin:5px 0;line-height:24px;background-color:white\"><span style=\"font-size:12px\">高新技术企业</span></p><p style=\"margin:5px 0;line-height:24px;background-color:white\"><span style=\"font-size:12px\">多项国家专利证书</span></p><p style=\"margin:5px 0;line-height:24px;background-color:white\"><span style=\"font-size:12px\">CCC </span><span style=\"font-size:12px\">中国国家强制性产品认证证书</span></p><p style=\"margin:5px 0;line-height:24px;background-color:white\"><span style=\"font-size:12px\">施耐德电气协议成套厂</span></p><p style=\"margin:5px 0;line-height:24px;background-color:white\"><span style=\"font-size:12px\">ABB</span><span style=\"font-size:12px\">低压开关柜合作伙伴</span></p><p style=\"margin:5px 0;line-height:24px;background-color:white\"><span style=\"font-size:12px\">西门子低压开关柜合作伙伴</span></p><p style=\"margin:5px 0;line-height:24px;background-color:white\"><span style=\"font-size:12px\">国家高压电器质量监督检验测试中心检验合格证书</span></p><p style=\"margin:5px 0;line-height:24px;background-color:white\"><span style=\"font-size:12px\">电力工业部电气设备质量检验测试中心检验合格证书</span></p><p style=\"margin:5px 0;line-height:24px;background-color:white\"><span style=\"font-size:12px\">采用国际标准产品标志证书</span></p><p style=\"margin:5px 0;line-height:24px;background-color:white\"><span style=\"font-size:12px\">质量信得过产品证书</span></p><p style=\"margin:5px 0;line-height:24px;background-color:white\"><span style=\"font-size:12px\">国家机械工业部和电力工业部联合颁发的高低压开关柜生产秩序整顿合格证书</span></p><p style=\"margin:5px 0;line-height:24px;background-color:white\"><span style=\"font-size:12px\">国家机械工业部和电力工业部联合颁发的低压成套开关设备生产秩序与产品质量整顿合格证书</span></p><p style=\"margin:5px 0;line-height:24px;background-color:white\"><span style=\"font-size:12px\">入围《国家经贸委第二、第三批全国城乡电网建设与改造所需主要设备产品企业推荐目录》</span></p><p><br /></p><p><br /></p>','2014-01-10 17:59:55',1389347991,1389347991),(7,18,'','','<p><strong><span style=\"color:#1F497D;font-family:宋体,simsun;font-size:16px;\"><span style=\"color:#1F497D;font-family:宋体,simsun;font-size:16px;\">一、盛隆宗</span><span style=\"color:#000000;font-family:arial;\"><span style=\"color:#1F497D;font-family:宋体,simsun;font-size:16px;\">旨</span></span><span style=\"color:#1F497D;font-family:宋体,simsun;font-size:16px;\">（1979年-1985年）</span></span></strong></p><p><strong><span style=\"color:#1F497D;font-family:宋体,simsun;font-size:16px;\"><span style=\"color:#1F497D;font-family:宋体,simsun;font-size:16px;\"></span></span></strong></p><p><br /></p><p style=\"text-align:justify;line-height:1.5em;text-indent:28px;margin-bottom:5px;\"><strong><span style=\"line-height:150%;font-family:楷体,楷体_gb2312, simkai;font-size:16px;\">盛隆宗旨（1979年）：<span style=\"line-height:150%;font-family:楷体,楷体_gb2312, simkai;font-size:16px;\"><img style=\"width:260px;height:257px;float:right;\" border=\"0\" hspace=\"0\" vspace=\"0\" src=\"http://m.shenglong-electric.com.cn/uploads/20130226/13618577337849.jpg\" width=\"260\" height=\"257\" /></span></span></strong></p><p style=\"text-align:justify;line-height:1.5em;text-indent:28px;\"><br /></p><p style=\"text-align:justify;line-height:1.5em;text-indent:28px;margin-bottom:5px;\"><span style=\"font-size:18px;\"></span><strong><span style=\"line-height:150%;font-family:楷体,楷体_gb2312, simkai;\"><span style=\"line-height:150%;font-family:楷体,楷体_gb2312, simkai;font-size:18px;\">为四化建设出力，为人民造福流汗</span></span></strong></p><p style=\"text-align:justify;line-height:1.5em;text-indent:28px;margin-bottom:5px;\"><strong><span style=\"line-height:150%;font-family:楷体,楷体_gb2312, simkai;\"><span style=\"line-height:150%;font-family:楷体,楷体_gb2312, simkai;font-size:18px;\"><br /></span></span></strong></p><p style=\"text-align:justify;line-height:1.5em;text-indent:28px;margin-bottom:5px;\"><span style=\"font-family:楷体,楷体_gb2312, simkai;font-size:20px;\"></span></p><p style=\"text-align:justify;line-height:1.5em;text-indent:28px;margin-bottom:5px;\"><strong><span style=\"font-family:楷体,楷体_gb2312, simkai;font-size:16px;\">1985</span><span style=\"font-family:楷体,楷体_gb2312, simkai;font-size:16px;\">年，盛隆宗旨发展为“四盛”：</span></strong></p><p style=\"text-align:justify;line-height:1.5em;text-indent:28px;margin-bottom:5px;\"><strong><span style=\"font-family:楷体,楷体_gb2312, simkai;\"></span></strong></p><p style=\"text-align:justify;line-height:1.5em;text-indent:32px;margin-bottom:5px;\"><strong><span style=\"color:black;font-family:楷体,楷体_gb2312, simkai;font-size:16px;\">盛隆宗旨：为四化建设，为人民造福</span></strong></p><p style=\"text-align:justify;line-height:1.5em;text-indent:32px;margin-bottom:5px;\"><strong><span style=\"color:black;font-family:楷体,楷体_gb2312, simkai;\"></span><span style=\"color:black;font-family:楷体,楷体_gb2312, simkai;\"><span style=\"color:black;font-family:楷体,楷体_gb2312, simkai;\"><span style=\"color:black;font-family:楷体,楷体_gb2312, simkai;font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;为国家分忧，</span><span style=\"font-family:楷体;font-size:16px;\">为群众解愁</span><span style=\"color:black;font-family:楷体,楷体_gb2312, simkai;font-size:16px;\">，</span></span></span></strong></p><p style=\"text-align:justify;line-height:1.5em;text-indent:32px;margin-bottom:5px;\"><strong><span style=\"color:black;font-family:楷体,楷体_gb2312, simkai;font-size:16px;\">盛隆思想：热爱国家，忠于公司</span></strong></p><p style=\"text-align:justify;line-height:1.5em;text-indent:32px;margin-bottom:5px;\"><strong><span style=\"color:black;font-family:楷体,楷体_gb2312, simkai;font-size:16px;\">盛隆作风：信誉至上，诚恳待人</span></strong></p><p style=\"text-align:justify;line-height:1.5em;text-indent:32px;margin-bottom:5px;\"><strong><span style=\"color:black;font-family:楷体,楷体_gb2312, simkai;font-size:16px;\">盛隆精神：艰苦奋斗，克己奉公</span></strong></p><p style=\"text-align:justify;line-height:1.5em;text-indent:32px;\"><br /></p><p style=\"text-align:justify;line-height:1.5em;text-indent:32px;margin-bottom:5px;\"><strong><span style=\"color:black;font-family:宋体,simsun;font-size:13px;\">1985</span><span style=\"color:black;font-family:宋体,simsun;font-size:13px;\">年，在公司成立六周年大会上，谢元德董事长要求全体盛隆人，一定要抱定盛隆宗旨，树立盛隆思想、保持盛隆作风，发扬盛隆精神，并且要把这“四盛”作为盛隆的传家宝，一代一代地传下去。</span></strong></p><p style=\"text-align:justify;line-height:1.5em;text-indent:32px;margin-bottom:5px;\"><strong><span style=\"color:black;font-family:宋体,simsun;font-size:13px;\"></span></strong>&nbsp;</p><p style=\"text-align:justify;line-height:1.5em;text-indent:32px;margin-bottom:5px;\"><strong><span style=\"color:black;font-family:宋体,simsun;font-size:13px;\">1992</span><span style=\"color:black;font-family:宋体,simsun;font-size:13px;\">年《盛隆之声》发刊词（摘要）</span><span style=\"color:black;\"></span><span style=\"color:black;font-family:宋体,simsun;font-size:13px;\">谢元德</span></strong></p><p style=\"text-align:justify;line-height:1.5em;text-indent:32px;margin-bottom:5px;\"><span style=\"color:black;font-family:宋体,simsun;font-size:13px;\">盛隆的创业并不是依靠雄厚的资金、先进的设备，盛隆的成就不仅仅是一排排楼房，一台台机器，盛隆的目标也不仅限于产值的增长，利税的提高。比这些更为重要的，是盛隆精神的培养和盛隆宗旨的发扬。过去我们靠它创业，今天我们靠它发展，未来我们靠它攀登。</span><br /></p>','2014-01-10 17:59:56',1389347991,1389347991),(8,19,'','','<p><strong><span style=\"font-family:宋体,simsun;color:#1f497d;font-size:16px\">二、盛隆理念（1994年）-&nbsp;盛世同襄盛举，隆情共创隆图</span></strong></p><p><strong><span style=\"font-family:宋体,simsun;color:#1f497d;font-size:16px\"></span></strong>&nbsp;</p><p><strong><span style=\"font-family:宋体,simsun;color:#1f497d;font-size:16px\"><img title=\"盛世同襄樊盛举\" src=\"http://m.shenglong-electric.com.cn/uploads/20130226/13618602745625.jpg\" /></span></strong></p>','2014-01-10 17:59:57',1389347991,1389347991),(9,20,'','','<p style=\"margin-bottom:20px;\"><strong><span style=\"color:#1F497D;font-family:宋体,simsun;font-size:16px;\">三、盛隆文化目标（2002年）</span></strong></p><p style=\"text-indent:28px;margin-top:10px;\"><strong><span style=\"font-family:楷体;font-size:16px;\">表层文化：温暖和谐、昂扬向上</span></strong></p><p style=\"text-indent:28px;\"><strong><span style=\"font-family:楷体;font-size:16px;\">中层文化：科学合理、易知易行</span></strong></p><p style=\"text-indent:28px;\"><strong><span style=\"font-family:楷体;font-size:16px;\">深层文化：追求卓越、奉献社会</span></strong></p><p style=\"margin:0px 0px 0px 28px;\"><span style=\"font-family:宋体;font-size:13px;\"></span></p><p style=\"text-align:justify;line-height:1.75em;\"><strong><span style=\"line-height:150%;font-family:楷体,楷体_gb2312, simkai;font-size:18px;\"></span></strong></p><p style=\"text-align:justify;\"><span style=\"font-size:18px;\"></span></p>','2014-01-10 17:59:57',1389347991,1389347991),(10,21,'','','<p><strong><span style=\"font-family:宋体,simsun;color:#1f497d;font-size:16px\">四、盛隆机制（2003年）-&nbsp;统分结合，个体为基；统分共利，集强个益</span></strong></p><p><strong><span style=\"font-family:宋体,simsun;color:#1f497d;font-size:16px\"></span></strong>&nbsp;</p><p><span style=\"font-family:宋体,simsun;color:#1f497d;font-size:16px\"></span></p><p style=\"line-height:1.5em;text-indent:35px\"><span style=\"font-family:宋体;color:black;font-size:13px\">大中含小，小中寓大；合中有分，分中有合。统而不僵，分而不散。把企业、集体（团队）、个人三者利益关系有机地、紧密地、融洽地结合起来。</span></p><p style=\"line-height:1.5em\"><span style=\"font-family:宋体,simsun;color:#1f497d;font-size:16px\"></span></p>','2014-01-10 17:59:58',1389347991,1389347991),(11,22,'','','<p><strong><span style=\"font-family:宋体,simsun;color:#1f497d;font-size:16px\">五、盛隆之歌（2009年）</span></strong></p><p><span style=\"font-family:宋体,simsun;font-size:13px\"></span></p><p><span style=\"font-family:宋体,simsun;font-size:13px\"><span style=\"font-family:&#39;calibri&#39;,&#39;sans-serif&#39;;font-size:14px\">&nbsp;</span></span></p><p><span style=\"font-family:宋体,simsun;font-size:13px\"><span style=\"font-family:&#39;calibri&#39;,&#39;sans-serif&#39;;font-size:14px\"><img title=\"盛隆之歌\" src=\"http://m.shenglong-electric.com.cn/uploads/20130226/1361868758718.png\" /></span></span></p>','2014-01-10 17:59:58',1389347991,1389347991),(12,23,'','','<p style=\"line-height:1.5em;margin-bottom:10px;\"><span style=\"color:#1F497D;font-family:宋体,simsun;font-size:16px;\"></span><strong><span style=\"color:#1F497D;font-family:宋体,simsun;font-size:16px;\">六、盛隆微（2009年）</span></strong></p><p style=\"line-height:1.5em;margin-bottom:20px;\"><strong><span style=\"color:#1F497D;font-family:宋体,simsun;font-size:16px;\"><span style=\"font-family:宋体;font-size:13px;\"><img style=\"width:300px;height:300px;float:right;\" border=\"0\" hspace=\"0\" vspace=\"0\" src=\"http://m.shenglong-electric.com.cn/uploads/20130301/13621049188461.jpg\" width=\"300\" height=\"300\" /></span></span></strong></p><p style=\"text-align:justify;line-height:1.5em;margin-bottom:20px;\"><span style=\"font-family:宋体;font-size:13px;\">盛隆徽说明:</span></p><p style=\"text-align:justify;line-height:1.5em;margin-bottom:20px;\"><span style=\"font-family:宋体;font-size:13px;\"></span></p><p style=\"text-align:justify;line-height:1.5em;margin-bottom:20px;\"><span style=\"font-family:宋体;font-size:13px;\">（</span><span style=\"font-family:宋体;font-size:13px;\">一）徽章由三圈同心圆组成</span><span style=\"font-size:13px;\">:</span><span style=\"font-family:宋体;font-size:13px;\">外层为盛隆电气公司的企业理念，由</span><span style=\"font-size:13px;\">16</span><span style=\"font-family:宋体;font-size:13px;\">个字组成，即</span><span style=\"font-size:13px;\">“</span><span style=\"font-family:宋体;font-size:13px;\">盛世同襄盛举，隆情共创隆图</span><span style=\"font-size:13px;\">”</span><span style=\"font-family:宋体;font-size:13px;\">。</span><span style=\"font-size:13px;\"></span><span style=\"font-family:宋体;font-size:13px;\">中层为盛隆电气的英文名称。内层是突出中国地图的地球。</span></p><p style=\"text-align:justify;line-height:1.5em;margin-bottom:20px;\"><span style=\"font-family:宋体;font-size:13px;\"></span></p><p style=\"text-align:justify;line-height:1.5em;margin-bottom:20px;\"><span style=\"font-family:宋体;font-size:13px;\"></span><span style=\"font-family:宋体;font-size:13px;\">（二）其内在含义包括以下几点：</span><span style=\"font-size:13px;\">1</span><span style=\"font-family:宋体;font-size:13px;\">、祖国是我们的核心，地球是我们的家园。这是盛隆的立足点和根本发展的灵魂。</span><span style=\"font-size:13px;\">2</span><span style=\"font-family:宋体;font-size:13px;\">、以盛隆理念，盛隆精神拥抱、回报祖国与地球家园。盛隆的发展目标是要做中国的盛隆，世界的盛隆。</span><span style=\"font-size:13px;\">3</span><span style=\"font-family:宋体;font-size:13px;\">、三圆同心象征圆满和谐；象征天时、地利、人和的融洽、包容。</span></p>','2014-01-10 17:59:59',1389347991,1389347991),(13,24,'','','<p style=\"line-height:1.5em\"><span style=\"color:#1f497d\"><strong><span style=\"font-size:16px\"><span style=\"font-family:宋体\"><span style=\"font-family:宋体;color:#1f497d;font-size:13px\"></span>七、盛隆旗（2009年）</span></span></strong></span></p><p style=\"line-height:1.5em\"><span style=\"color:#1f497d\"><strong><span style=\"font-size:16px\"><span style=\"font-family:宋体\"></span></span></strong></span>&nbsp;</p><p><span style=\"font-family:宋体,simsun;font-size:13px\"></span></p><p style=\"line-height:1.5em\"><span style=\"font-family:宋体,simsun;font-size:13px\">盛隆旗帜说明：</span></p><p style=\"line-height:1.5em\"><span style=\"font-family:宋体,simsun;font-size:13px\"></span>&nbsp;</p><p style=\"line-height:1.5em\"><span style=\"font-family:宋体,simsun;font-size:13px\">&nbsp;&nbsp;&nbsp;&nbsp;盛隆旗由红、白、蓝三种颜色组成。红色、蓝色取自盛隆注册商标的两种颜色。红色代表盛隆积极攀登、拼搏奋斗的生气与活力；蓝色代表盛隆尊重科学、沉着务实的冷静与理性；白色是日光七色的总和，代表盛隆不拘一格、天地广阔的开放与包容。</span></p><p style=\"line-height:1.5em\"><span style=\"font-family:宋体,simsun;font-size:13px\"></span>&nbsp;</p><p><span style=\"font-family:宋体,simsun;font-size:13px\"></span></p><p><span style=\"font-family:宋体,simsun;font-size:13px\"></span></p><p><span style=\"font-family:宋体,simsun;font-size:13px\"><img style=\"width:691px;height:150px\" title=\" 盛隆旗\" src=\"http://m.shenglong-electric.com.cn/uploads/20130228/13620340763544.png\" width=\"262\" height=\"33\" /></span></p>','2014-01-10 18:00:00',1389347991,1389347991),(14,25,'','','<p><strong><span style=\"font-family:宋体,simsun;color:#1f497d;font-size:16px\">八、盛隆法则（2010年）</span></strong></p><p><strong><span style=\"font-family:宋体,simsun;color:#1f497d;font-size:16px\"></span></strong></p><p><strong><span style=\"font-family:宋体,simsun;color:#1f497d;font-size:16px\"></span></strong>&nbsp;</p><p style=\"text-align:center;line-height:150%\"><strong><span style=\"line-height:150%;font-family:楷体,楷体_gb2312, simkai;font-size:18px\">品质信誉升升升</span></strong></p><p style=\"text-align:center;line-height:150%\"><strong><span style=\"line-height:150%;font-family:楷体,楷体_gb2312, simkai;font-size:18px\">虚华成本降降降</span></strong></p><p style=\"text-align:center;line-height:150%\"><strong><span style=\"line-height:150%;font-family:楷体,楷体_gb2312, simkai;font-size:18px\">集约经营管管管</span></strong></p><p style=\"text-align:center;line-height:150%\"><strong><span style=\"line-height:150%;font-family:楷体,楷体_gb2312, simkai;font-size:18px\">用户利益让让让</span></strong></p><p style=\"text-align:center;line-height:150%\"><strong><span style=\"line-height:150%;font-family:楷体,楷体_gb2312, simkai;font-size:18px\"></span></strong>&nbsp;</p><p style=\"text-align:center;line-height:150%\"><strong><span style=\"line-height:150%;font-family:宋体,simsun;font-size:13px\"></span></strong></p><p style=\"text-align:justify;line-height:1.5em;text-indent:28px\"><span style=\"line-height:150%;font-family:宋体;color:black;font-size:13px\">经过</span><span style=\"line-height:150%;font-family:calibri;color:black;font-size:13px\">30</span><span style=\"line-height:150%;font-family:宋体;color:black;font-size:13px\">多年实践检验的盛隆文化，是具有强大生命活力的企业创举。具有中国特色的盛隆群体老板体制的成功，必然带来行业的集约化，科学化升级转型。带给用户品质高端、价格低廉的产品和服务。</span></p><p style=\"text-align:center;line-height:150%\"><strong><span style=\"line-height:150%;font-family:宋体,simsun;font-size:13px\"></span></strong></p><p><strong><span style=\"font-family:宋体,simsun;color:#1f497d;font-size:16px\"></span></strong></p>','2014-01-10 18:00:01',1389347991,1389347991),(15,26,'','','<p style=\"line-height:1.5em\"><strong><span style=\"font-family:宋体,simsun;color:#1f497d;font-size:16px\">九、盛隆伦理（2011年）-&nbsp;信任、关爱、诚实做人、踏实做事</span></strong></p><p style=\"line-height:1.5em\"><strong><span style=\"font-family:宋体,simsun;color:#1f497d;font-size:16px\"></span></strong>&nbsp;</p><p style=\"line-height:1.5em\"><strong><span style=\"font-family:宋体,simsun;color:#1f497d;font-size:16px\"></span></strong></p><p><strong><span style=\"line-height:150%;font-family:宋体;color:black;font-size:13px\">做金牌英雄</span><span style=\"line-height:150%;color:black;font-size:16px\"></span><span style=\"line-height:150%;font-family:宋体;color:black;font-size:13px\">创金牌盛隆——《盛隆电气英雄奖章》颁行辞</span></strong></p><p><strong><span style=\"line-height:150%;font-family:宋体;color:black;font-size:13px\"></span></strong>&nbsp;</p><p><span style=\"line-height:150%;font-family:宋体;color:black;font-size:13px\"></span></p><p><span style=\"line-height:150%;font-family:宋体;color:black;font-size:16px\"></span><span style=\"line-height:150%;font-family:宋体;color:black;font-size:13px\">&nbsp;&nbsp;&nbsp;&nbsp;盛世盛隆花开红，盛隆沃土出英雄。<img style=\"width:300px;float:right;height:300px\" title=\"盛隆伦理\" border=\"0\" hspace=\"0\" src=\"http://m.shenglong-electric.com.cn/uploads/20130301/13621071091905.jpg\" width=\"300\" height=\"300\" /></span></p><p><span style=\"line-height:150%;font-family:宋体;color:black;font-size:13px\">&nbsp;&nbsp;&nbsp;&nbsp;豪迈走向大时代，为国为民争先锋。</span></p><p><span style=\"line-height:150%;font-family:宋体;color:black;font-size:16px\"></span><span style=\"line-height:150%;font-family:宋体;color:black;font-size:13px\">&nbsp;&nbsp;&nbsp;&nbsp;盛隆英雄是盛隆精神的力行者，是盛隆文化的弘扬者。</span></p><p><span style=\"line-height:150%;font-family:宋体;color:black;font-size:13px\">&nbsp;&nbsp;&nbsp;&nbsp;是盛隆形象的体现和引领者，是盛隆科学发展、</span></p><p><span style=\"line-height:150%;font-family:宋体;color:black;font-size:13px\">&nbsp;&nbsp;&nbsp;&nbsp;百年辉煌的先锋模范，是盛隆人的优秀、杰出代表。</span></p><p><span style=\"line-height:150%;font-family:宋体;color:black;font-size:13px\">&nbsp;&nbsp;&nbsp;&nbsp;信任、关爱，诚实做人、踏实做事，这是盛隆伦理；</span></p><p><span style=\"line-height:150%;font-family:宋体;color:black;font-size:16px\"></span><span style=\"line-height:150%;font-family:宋体;color:black;font-size:13px\">&nbsp;&nbsp;&nbsp;&nbsp;实干受实惠，正道有正果，这是盛隆法则。</span></p><p><span style=\"line-height:150%;font-family:宋体;color:black;font-size:16px\"></span><span style=\"line-height:150%;font-family:宋体;color:black;font-size:13px\">&nbsp;&nbsp;&nbsp;&nbsp;金牌诚可贵，英雄格更高。</span></p><p><span style=\"line-height:150%;font-family:宋体;color:black;font-size:16px\"></span><span style=\"line-height:150%;font-family:宋体;color:black;font-size:13px\">&nbsp;&nbsp;&nbsp;&nbsp;盛隆人是奇迹和金牌的创造者。</span></p><p><span style=\"line-height:150%;font-family:宋体;color:black;font-size:16px\"></span><span style=\"line-height:150%;font-family:宋体;color:black;font-size:13px\">&nbsp;&nbsp;&nbsp;&nbsp;众人划桨破浪东，百年辉煌我盛隆。</span></p><p><span style=\"line-height:150%;font-family:宋体;color:black;font-size:16px\"></span><span style=\"line-height:150%;font-family:宋体;color:black;font-size:13px\">&nbsp;&nbsp;&nbsp;&nbsp;盛隆英雄齐努力，金牌盛隆立寰中！</span></p><p><span style=\"line-height:150%;font-family:宋体;color:black;font-size:16px\"></span><span style=\"line-height:150%;font-family:宋体;color:black;font-size:13px\">&nbsp;&nbsp;&nbsp;&nbsp;盛隆金牌，千赤足金。献与英雄，表彰功勋。</span></p><p><span style=\"line-height:150%;font-family:宋体;color:black;font-size:16px\"></span><span style=\"line-height:150%;font-family:宋体;color:black;font-size:13px\">&nbsp;&nbsp;&nbsp;&nbsp;美好家园，以人为本。百年辉煌，看我群英。</span></p><p><span style=\"line-height:150%;font-family:宋体;color:black;font-size:17px\"></span></p><p><strong><span style=\"font-family:宋体,simsun;color:#1f497d;font-size:13px\"></span></strong></p>','2014-01-10 18:00:04',1389347991,1389347991),(16,27,'','','<p><strong><span style=\"font-size:16px;color:#1f497d;font-family:宋体,simsun\">十、盛隆哲学（2012年）-&nbsp;帮助别人就是成全自己、成功他人定能实现自我</span></strong></p><p><span style=\"font-size:13px;font-family:宋体,simsun\"></span>&nbsp;</p><p><span style=\"font-size:13px;font-family:宋体,simsun\"></span>&nbsp;</p><p style=\"text-indent:28px;line-height:1.5em;margin-bottom:15px;\"><span style=\"font-size:13px;font-family:宋体\">哲学是世界观、方法论，是文化的顶级和核心。</span><span style=\"font-size:13px;color:black;font-family:宋体\">盛隆企业文化的体系和内容都是以盛隆哲学为指导的。什么是盛隆哲学呢？回顾</span><span style=\"font-size:13px;color:black;font-family:calibri\">30</span><span style=\"font-size:13px;color:black;font-family:宋体\">多年的发展之路，分析盛隆的企业文化，我们把它归结为：帮助别人就是成全自己，成功他人定能实现自我。中国儒学圣人孔子说过</span><span style=\"color:black\"></span><span style=\"font-size:13px;color:black;font-family:宋体\">：仁者，“己欲立而立人，己欲达而达人。”意思是，一个讲究仁爱的人，要使自己建功立业，发达有成，就必须同时关心他人的生存发展。帮助他人达到“立”功、“立”业，和通“达</span><span style=\"font-size:13px;color:black;font-family:calibri\">”</span><span style=\"font-size:13px;color:black;font-family:宋体\">、发“达”。孔子这一思想，是中国优秀传统文化的重要内容。公司董事长小时候听母亲所讲范曾的故事，就是对孔子理论最好的实践和验证。范曾千辛万苦去“问天”，一心帮别人除困解难，</span><span style=\"color:black\"></span><span style=\"font-size:13px;color:black;font-family:宋体\">自己的困惑难题一句话也没有提。然而，就在他一心一意帮别人解难的过程中，他毫无所求，却既意外、而又自然地得到了金银珠宝、美丽新娘。孔子的理论、范曾的故事，表达了同一个思想：社会的凝聚、和谐，生活的创造、美好，个人的成功、发展，需要善意、善举、善人。需要与他人、团队、社会同心协力、携手而行。</span>&nbsp;<span style=\"font-size:12px\"></span></p><p style=\"text-indent:28px;line-height:1.5em;margin-bottom:15px;\"><span style=\"font-size:13px;color:black;font-family:calibri\">30</span><span style=\"font-size:13px;color:black;font-family:宋体\">多年来，盛隆的文化与此一脉相承。“真心圆你一个梦，真情帮你去成功。”察己知人、推己及人，急人所急，帮人所需，以至先人后己、厚人薄己，成为公司推崇、传承的美德良风；成为一种文化伦理、哲学理念。深深影响到盛隆的创业、立业和经营发展。</span>&nbsp;</p><p style=\"text-indent:28px;line-height:1.5em;margin-bottom:15px;\"><span style=\"font-size:13px;color:black;font-family:宋体\">由这一基本理念出发，公司创造了以千方百计帮就业、帮创业、帮立业为核心目标，温暖和谐，昂扬向上，追求卓越，奉献社会的企业文化。建立了“信任、关爱”的企业伦理。形成了诚实做人，踏实做事，走正道，成正果的机制制度和良好的风气与氛围。好人定有好报，实干必得实惠，老实人终究不吃亏；先做人，后做事；帮人是做人的本分，助人是做事的前提等等，已成为盛隆人信奉、遵循的做人准则和生活信条。</span>&nbsp;</p><p style=\"text-indent:28px;line-height:1.5em;margin-bottom:15px;\"><span style=\"font-size:13px;color:black;font-family:calibri\">30</span><span style=\"font-size:13px;color:black;font-family:宋体\">几年来盛隆的最大成就，最主要地不是厂房、设备旧貌换新颜，不是产值、利润成倍翻番，也不是个人收入财富大幅增长；而是在盛隆这个大学校里、大平台上，培养、帮助了一代代年轻人就业，创业，立业；成事，成人，成才。不仅使他们个人、家庭的命运发生了根本性改变，而且带动、帮助家乡和远近一方的父老乡亲扩大就业，帮助地方解决就业之难，为国分忧，产生了广泛的社会影响和良好的社会效益。</span>&nbsp;</p><p style=\"text-indent:28px;line-height:1.5em;margin-bottom:15px;\"><span style=\"font-size:13px;color:black;font-family:宋体\">盛隆老一代的创业者，少壮派的掌门人，</span><span style=\"font-size:13px;color:black;font-family:calibri\">80</span><span style=\"font-size:13px;color:black;font-family:宋体\">、</span><span style=\"font-size:13px;color:black;font-family:calibri\">90</span><span style=\"font-size:13px;color:black;font-family:宋体\">后的佼佼者，他们都是因为主动关心帮助他人，全心全意培养、扶持年轻人，努力建好团队、服务团队，沿着“支持别人，就是完善自己；帮助别人，就是成全自己”，“达人而后达己”，走向成功的。他们的经验反复证明，在盛隆哲学指导下的立人、达人，这正是盛隆源源不绝的生命活力所在，是盛隆长盛不衰的凝聚力、创造力所在；是盛隆文化价值的核心，是最重要的软实力、竞争力。</span>&nbsp;</p><p style=\"text-indent:28px;line-height:1.5em;margin-bottom:15px;\"><span style=\"font-size:13px;color:black;font-family:宋体\">西方的经济理论认为，市场经济的参与者，是主观为自己，客观为他人。这种理论助长了市场经济的负面作用。而中国优秀传统文化中“立人己立，达人己达”的理论，包含着科学的道理，具有人性的力量，体现了民族的美德。能使社会更加温暖和谐，激发凝聚力、创造力。盛隆把这一理论作为企业哲学，取得了巨大的成功。它同时也证明，盛隆文化既有时代的先进性，又有优秀的传统性，广泛的群众性。因而它必然具有深厚的生命根基，旺盛的生机活力。</span></p><p style=\"line-height:1.5em;margin-bottom:15px;\"><span style=\"font-size:13px;font-family:宋体,simsun\"></span>&nbsp;</p>','2014-01-10 18:00:05',1389347991,1389347991),(17,20,'','','<p><span style=\"font-family:宋体;font-size:13px;\">K<span style=\"color:black;\">YN61-40.5</span></span><span style=\"color:black;font-family:宋体;font-size:13px;\">户内交流金属铠装抽出式开关柜<img title=\"高压系列.jpg\" style=\"float:right;\" src=\"http://m.shenglong-electric.com.cn/uploads/20130403/13649716778769.jpg\" /></span></p><p><span style=\"color:black;font-family:宋体;font-size:13px;\">KYN28-12</span><span style=\"color:black;font-family:宋体;font-size:13px;\">户内交流金属铠装抽出式开关柜</span></p><p><span style=\"color:black;font-family:宋体;font-size:13px;\">KYN28-24&nbsp;</span><span style=\"color:black;font-family:宋体;font-size:13px;\">户内交流金属铠装移开式开关柜</span></p><p><span style=\"color:black;font-family:宋体;font-size:13px;\">UniGearZS1</span><span style=\"color:black;font-family:宋体;font-size:13px;\">铠装式金属封闭开关设备（ABB授权产品）</span></p><p><span style=\"color:black;font-family:宋体;font-size:13px;\">KYN1-10</span><span style=\"color:black;font-family:宋体;font-size:13px;\">型交流金属铠装移开式开关柜</span></p><p><span style=\"color:black;font-family:宋体;font-size:13px;\">JYN2-10</span><span style=\"color:black;font-family:宋体;font-size:13px;\">型交流金属间隔移开式开关柜</span></p><p><span style=\"color:black;font-family:宋体;font-size:13px;\">GG-1A</spa